function init_player() {
    var n = document.getElementsByClassName("vjs-loading-spinner"),
        s = document.getElementById("overlay"),
        i = document.getElementById("overlay-stop"),
        d = document.getElementById("overlay-play"),
        l = document.getElementById("title-overlay");
    l.innerHTML = e.title_ads, 
    video = videojs("video_1", { 
    	poster: "http://content.bitsontherun.com/thumbs/3XnJSIm4-480.jpg", 
    	preload: true,
    	autoplay: false,
    	width: 490,
    	height: 275,
    	type: "video/mp4",
    	src: "http://content.bitsontherun.com/videos/bkaovAYt-52qL9xLP.mp4",
    }), 
    video.on("ended", function() { 
    	this.posterImage.show(),
    	noti_end(e.id_frame) 
   	}), 
   	video.on("play", function() { 
   		this.trigger("loadstart"), 
   		s.style.display = "none", 
   		i.style.display = "block", 
   		d.style.display = "none", 
   		this.posterImage.hide(), 
   		video.bigPlayButton.hide(), 
   		l.style.display = "none", 
   		0 == checkPlay && (ga("send", { 
   			hitType: "pageview", 
   			page: e.resource, 
   			title: e.title_ads 
   		}), noti_play(e.id_frame), checkPlay = 1), 
   		n[0].style.display = "block" 
   	}), 
   	video.on("playing", function() { 
   		n[0].style.display = "none" 
   	}), 
   	video.on("pause", function() { 
   		s.style.display = "block", 
   		i.style.display = "none", 
   		d.style.display = "block", 
   		video.bigPlayButton.show(), 
   		noti_pause(e.id_frame) 
   	}), 
   	video.on("mouseover", function(e) { 0 == check_mouseover && (check_mouseover = 1) }), 
   	video.on("seeking", function() { n[0].style.display = "block" }), 
   	i.addEventListener("mouseover", function(e) { 0 == check_mouseover && (check_mouseover = 1) }), 
   	i.addEventListener("mouseover", function() { 
   		addClass(document.getElementsByClassName("vjs-control-bar")[0], "showControl") 
   	}), 
   	i.addEventListener("mouseout", function() { 
   		removeClass(document.getElementsByClassName("vjs-control-bar")[0], "showControl") 
   	}), 
   	video.vastClient({ adTagUrl: e.ads_link, vpaidFlashLoaderPath: '' }),
   	loadOverlay(video)
}

function addClass(e, o) { e.classList ? e.classList.add(o) : hasClass(e, o) || (e.className += " " + o) }

function removeClass(e, o) {
    if (e.classList) e.classList.remove(o);
    else if (hasClass(e, o)) {
        var t = new RegExp("(\\s|^)" + o + "(\\s|$)");
        e.className = e.className.replace(t, " ") } }

function videoHoverLoading(e) {
    var o = document.getElementById("adtima_player");
    0 == startLoading && (addClass(o, "loading"), setTimeout(function() { removeClass(o, "loading"), startLoading = 1, e.play() }, 1200)) }

function loadOverlay(e) {
    function o() { e.play(), s.style.display = "block", n.style.display = "none" }

    function t() { e.pause(), s.style.display = "none", n.style.display = "block" }
    var a = document.getElementById("title-overlay"),
        n = document.getElementById("overlay-play"),
        s = document.getElementById("overlay-stop");
    n.addEventListener("click", o), s.addEventListener("click", t), e.on("ended", function() { s.style.display = "none", n.style.display = "block", a.style.display = "block" }) }

function noti_play(e) {
    if (void 0 != e && "" != e) try { parent.adtima_player_play(e) } catch (o) {} }

function noti_end(e) {
    if (void 0 != e && "" != e) try { parent.adtima_player_end(e) } catch (o) {} }

function noti_pause(e) {
    if (void 0 != e && "" != e) try { parent.adtima_player_pause(e) } catch (o) {} }

function adtima_player_play() { video.play() }

function adtima_player_pause() { video.pause() }

function adtima_player_mute(e) { video.muted(e) }
